import React from 'react'

const Table = ({ data, onDelete }) => {
    return (
        <table className='.manage-table table {
            overflow: hidden;
            width: 100%;
            border-collapse: collapse;
            margin-top: 20px; 
            overflow: scroll;
         }
          
          
          .manage-table th, td {
            padding: 12px;
            text-align: left;
            overflow: hidden;
            border-bottom: 1px solid #ddd;
          }
        
        
        
          .manage-table th {
            background-color: #00000098;
            color: #fff;
          }
          
          .manage-table tr:hover {
            background-color: #f5f5f5;
          }
        
          .manage-table .delete{
            margin-bottom: 15px;
            padding: 10px;
            margin-left: 15px;
            background-color: #a71a1a;
            color: #fff;
            border: none;
            border-radius: 3px;
            cursor: pointer;
          }
          ' >
            <thead>
                <tr>
                    <th>Username</th>
                    <th>Story</th>
                    <th>Likes</th>
                    <th>Action</th>
                </tr>
            </thead>
            <tbody>
                {data.map((item) => (
                    <tr key={item.id}>
                        <td>{item.username}</td>
                        <td>{item.story}</td>
                        <td>{item.likes}</td>
                        <td>
                            <button onClick={() => onDelete(item.id)}>Delete</button>
                        </td>
                    </tr>
                ))}
            </tbody>
        </table>
    );
};

export default Table
